def intToDecimal(qty, decimal):
    return int(qty * int("".join(["1"] + ["0"] * decimal)))


def get_api_call_data(url):
    try:
        call_data = requests.get(url)
    except Exception as ex:
        print(ex)
        return get_api_call_data(url)
    try:
        api_data = call_data.json()
        return api_data
    except Exception as ex:
        print(ex)
        print(call_data.text)


def inch_swap(privatekey, amount_to_swap, fromTokenAddress, to_symbol):
    global address_wallet
    try:
        amount = intToDecimal(amount_to_swap, 18)
        _1inchurl = f'https://api.1inch.io/v4.0/42161/swap?fromTokenAddress={to_token_address}&toTokenAddress={fromTokenAddress}&amount={amount}&fromAddress={address_wallet}&slippage=1'
        json_data = get_api_call_data(_1inchurl)
        nonce = web3.eth.getTransactionCount(address_wallet)
        tx = json_data['tx']
        tx['nonce'] = nonce
        tx['to'] = Web3.toChecksumAddress(tx['to'])
        tx['gasPrice'] = int(tx['gasPrice'])
        tx['value'] = int(tx['value'])
        signed_tx = web3.eth.account.signTransaction(tx, privatekey)
        tx_hash = web3.eth.sendRawTransaction(signed_tx.rawTransaction)

        cprint(f'\n>>> Swap {to_symbol} : https://arbiscan.io/tx/{web3.toHex(tx_hash)}', 'green')
    except Exception as error:
        cprint(f'\n>>> Ошибка | {address_wallet} | {to_symbol} | {error}', 'red')


def inch_swap_approve(privatekey, amount, fromTokenAddress, to_symbol):
    global address_wallet
    try:
        _1inchurl = f'https://api.1inch.io/v4.0/42161/approve/transaction?tokenAddress={fromTokenAddress}&amount={amount}'
        json_data = get_api_call_data(_1inchurl)
        nonce = web3.eth.getTransactionCount(address_wallet)
        tx = {
            "nonce": nonce,
            "to": web3.toChecksumAddress(json_data["to"]),
            "data": json_data["data"],
            "gasPrice": web3.eth.gas_price,
            "gas": gasLimit,
        }

        signed_tx = web3.eth.account.signTransaction(tx, privatekey)
        tx_hash = web3.eth.sendRawTransaction(signed_tx.rawTransaction)

        cprint(f'\n>>> approve {to_symbol} : https://arbiscan.io/tx/{web3.toHex(tx_hash)}', 'green')
    except Exception as error:
        cprint(f'\n>>> Ошибка | {address_wallet} | {to_symbol} | {error}', 'red')


def inch_swap_sell(privatekey, fromTokenAddress, to_symbol):
    global address_wallet, to_token_address
    try:
        if to_symbol == 'DBL':
            ABI = ABI_DBL
        elif to_symbol == 'LPT':
            ABI = ABI_LPT
        elif to_symbol == 'USDT':
            ABI = ABI_USDT
        elif to_symbol == 'USDC':
            ABI = ABI_USDT
        elif to_symbol == 'DAI':
            ABI = ABI_DAI
        else:
            return
        token = web3.eth.contract(address=fromTokenAddress, abi=ABI)  # declaring the token contract
        token_balance = token.functions.balanceOf(address_wallet).call()
        amount = token_balance
        inch_swap_approve(privatekey, amount, fromTokenAddress, to_symbol)
        time.sleep(random.randint(10, 15))
        _1inchurl = f'https://api.1inch.io/v4.0/42161/swap?fromTokenAddress={fromTokenAddress}&toTokenAddress={to_token_address}&amount={amount}&fromAddress={address_wallet}&slippage=3'
        json_data = get_api_call_data(_1inchurl)
        nonce = web3.eth.getTransactionCount(address_wallet)
        tx = json_data['tx']
        tx['nonce'] = nonce
        tx['to'] = Web3.toChecksumAddress(tx['to'])
        tx['gasPrice'] = int(tx['gasPrice'])
        tx['value'] = int(tx['value'])
        signed_tx = web3.eth.account.signTransaction(tx, privatekey)
        tx_hash = web3.eth.sendRawTransaction(signed_tx.rawTransaction)

        cprint(f'\n>>> Sell {to_symbol} : https://arbiscan.io/tx/{web3.toHex(tx_hash)}', 'green')
    except Exception as error:
        cprint(f'\n>>> Ошибка | {address_wallet} | {to_symbol} | {error}', 'red')


